'use client'
import Image from 'next/image'

export default function SimpleImage({ src, alt, width = 400, height = 400, className = '', ...props }) {
  return (
    <Image
      src={src}
      alt={alt}
      width={width}
      height={height}
      className={className}
      unoptimized={true}
      {...props}
      onError={(e) => {
        e.target.src = 'data:image/svg+xml;base64,'
      }}
    />
  )
}